import React, { useEffect, useState } from 'react';
import api from '../api/api';
import { Link } from 'react-router-dom';

function LiveScores() {
  const [scores, setScores] = useState([]);

  useEffect(() => {
    api.get('/livescores').then((res) => setScores(res.data.response));
  }, []);

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4 text-gray-800">⚽ Live Scores</h1>

      {scores.map((s, index) => (
        <Link
          key={index}
          to={`/fixture/${s.fixture.id}`}
          className="block bg-white rounded-lg shadow hover:shadow-lg transition duration-300 p-4 mb-4 border border-gray-200"
        >
          <div className="flex justify-between items-center">
            <div className="text-lg font-semibold text-gray-900">
              {s.teams.home.name} vs {s.teams.away.name}
            </div>
            <div className="text-xl font-bold text-green-600">
              {s.goals.home} - {s.goals.away}
            </div>
          </div>
          <p className="text-sm text-gray-500 mt-2">Clique para ver detalhes da partida →</p>
        </Link>
      ))}
    </div>
  );
}

export default LiveScores;
