import React, { useEffect, useState } from 'react';
import api from '../api/api';
import { Link } from 'react-router-dom';

function LiveScores() {
  const [scores, setScores] = useState([]);
  useEffect(() => {
    api.get('/livescores').then(res => setScores(res.data));
  }, []);
  return (
    <div>
      <h1 className="text-xl p-4">Live Scores</h1>
      {scores.map(s => (
        <div key={s.id} className="card">
          <Link to={'/fixture/' + s.id}>{s.home} vs {s.away} - {s.score}</Link>
        </div>
      ))}
    </div>
  );
}

export default LiveScores;
