import React, { useEffect, useState } from 'react';
import api from '../api/api';
import { Link } from 'react-router-dom';

function LiveScores() {
  const [scores, setScores] = useState([]);

  useEffect(() => {
    api.get('/livescores')
      .then((res) => setScores(res.data.response))
      .catch((err) => console.error('Erro ao buscar live scores:', err));
  }, []);

  return (
    <div className="max-w-5xl mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">ðŸ”´ Live Scores</h1>

      {scores.length === 0 ? (
        <p className="text-gray-600">Nenhum jogo ao vivo no momento.</p>
      ) : (
        scores.map((s, index) => (
          <Link
            key={index}
            to={`/fixture/${s.fixture.id}`}
            className="block bg-white rounded-xl shadow-md hover:shadow-lg transition duration-300 p-4 mb-4 border border-gray-200"
          >
            <div className="flex items-center justify-between">
              {/* Time Casa */}
              <div className="flex items-center gap-2">
                <img src={s.teams.home.logo} alt={s.teams.home.name} className="w-8 h-8" />
                <span className="font-semibold text-gray-900">{s.teams.home.name}</span>
              </div>

              {/* Placar */}
              <div className="text-xl font-bold text-green-600">
                {s.goals.home} - {s.goals.away}
              </div>

              {/* Time Visitante */}
              <div className="flex items-center gap-2">
                <span className="font-semibold text-gray-900">{s.teams.away.name}</span>
                <img src={s.teams.away.logo} alt={s.teams.away.name} className="w-8 h-8" />
              </div>
            </div>

            {/* Status do Jogo */}
            <p className="text-sm text-gray-500 mt-2">{s.fixture.status.long}</p>
          </Link>
        ))
      )}
    </div>
  );
}

export default LiveScores;
