import React, { useEffect, useState } from 'react';
import api from '../api/api';

function Standings() {
  const [table, setTable] = useState([]);

  useEffect(() => {
    api.get('/standings').then((res) => {
      const league = res.data.response[0]?.league;
      const standingsArray = league?.standings[0] || [];
      setTable(standingsArray);
    }).catch((err) => console.error('Erro ao buscar standings:', err));
  }, []);

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4 text-gray-800">üèÜ Standings</h1>

      {table.length === 0 ? (
        <p className="text-gray-600">Nenhum dado de classifica√ß√£o encontrado.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white shadow-md rounded-lg overflow-hidden">
            <thead className="bg-gray-900 text-white">
              <tr>
                <th className="py-2 px-4 text-left">Pos</th>
                <th className="py-2 px-4 text-left">Team</th>
                <th className="py-2 px-4 text-center">Points</th>
              </tr>
            </thead>
            <tbody>
              {table.map((team) => (
                <tr key={team.rank} className="border-b">
                  <td className="py-2 px-4">{team.rank}</td>
                  <td className="py-2 px-4 flex items-center gap-2">
                    <img src={team.team.logo} alt={team.team.name} className="w-6 h-6" />
                    {team.team.name}
                  </td>
                  <td className="py-2 px-4 text-center">{team.points}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

    </div>
  );
}

export default Standings;
