import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api/api';

function FixtureDetails() {
  const { id } = useParams();
  const [fixture, setFixture] = useState(null);

  useEffect(() => {
    api.get('/fixture/' + id)
      .then((res) => setFixture(res.data.response[0]))
      .catch((err) => console.error('Erro ao buscar fixture details:', err));
  }, [id]);

  if (!fixture) return <p className="p-4">Carregando detalhes da partida...</p>;

  return (
    <div className="max-w-5xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6 text-gray-800">
        Detalhes da Partida
      </h1>

      <div className="bg-white rounded-xl shadow p-4 mb-6">
        <div className="flex items-center justify-between">
          {/* Time Casa */}
          <div className="flex items-center gap-2">
            <img src={fixture.teams.home.logo} alt={fixture.teams.home.name} className="w-8 h-8" />
            <span className="font-semibold">{fixture.teams.home.name}</span>
          </div>

          {/* Placar */}
          <div className="text-2xl font-bold">
            {fixture.goals.home} - {fixture.goals.away}
          </div>

          {/* Time Visitante */}
          <div className="flex items-center gap-2">
            <span className="font-semibold">{fixture.teams.away.name}</span>
            <img src={fixture.teams.away.logo} alt={fixture.teams.away.name} className="w-8 h-8" />
          </div>
        </div>

        {/* Status */}
        <p className="text-sm text-gray-500 mt-2">{fixture.fixture.status.long}</p>
      </div>

      {/* Estatísticas */}
      {fixture.statistics && fixture.statistics.length > 0 && (
        <div className="bg-white rounded-xl shadow p-4">
          <h2 className="text-xl font-semibold mb-4">Estatísticas do Jogo</h2>
          <ul className="space-y-2">
            {fixture.statistics.map((stat, index) => (
              <li key={index} className="flex justify-between text-sm text-gray-700">
                <span>{stat.type}</span>
                <span>
                  {stat.statistics[0]?.value || 0} - {stat.statistics[1]?.value || 0}
                </span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default FixtureDetails;
