import React, { useEffect, useState } from 'react';
import api from '../api/api';

function UpcomingFixtures() {
  const [fixtures, setFixtures] = useState([]);

  useEffect(() => {
    api.get('/fixtures').then((res) => setFixtures(res.data));
  }, []);

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6 text-gray-800">ðŸ“… Upcoming Fixtures</h1>

      {fixtures.length === 0 ? (
        <p className="text-gray-600">No upcoming matches found.</p>
      ) : (
        fixtures.map((f) => (
          <div
            key={f.id}
            className="bg-white border-l-4 border-blue-600 shadow-md rounded-lg p-4 mb-4 hover:shadow-lg transition"
          >
            <div className="flex justify-between items-center mb-2">
              <span className="text-lg font-semibold text-gray-900">
                {f.home} vs {f.away}
              </span>
              <span className="text-sm text-gray-500">{f.date}</span>
            </div>
            <p className="text-sm text-gray-600">Click to view match details...</p>
          </div>
        ))
      )}
    </div>
  );
}

export default UpcomingFixtures;
