import React, { useEffect, useState } from 'react';
import api from '../api/api';
import { Link } from 'react-router-dom';

function LiveScores() {
  const [scores, setScores] = useState([]);

  useEffect(() => {
    api.get('/livescores').then((res) => setScores(res.data.response));
  }, []);

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4 text-gray-800">âš½ Live Scores</h1>

      {scores.map((s, index) => (
        <div key={index} className="bg-white shadow rounded p-4 mb-4">
          <p className="text-lg font-semibold">
            {s.teams.home.name} vs {s.teams.away.name}
          </p>
          <p>
            Score: {s.goals.home} - {s.goals.away}
          </p>
          <p>
            Full Time: {s.score.fulltime || 'N/A'}
          </p>
          <p>
            Halftime: {s.score.halftime || 'N/A'}
          </p>
        </div>
      ))}

    </div>
  );
}

export default LiveScores;
