import React, { useEffect, useState } from 'react';
import api from '../api/api';
import { Link } from 'react-router-dom';

function UpcomingFixtures() {
  const [fixtures, setFixtures] = useState([]);

  useEffect(() => {
    api.get('/fixtures')
      .then((res) => setFixtures(res.data.response))
      .catch((err) => console.error('Erro ao buscar fixtures:', err));
  }, []);

  return (
    <div className="max-w-5xl mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">ðŸ“… PrÃ³ximos Jogos</h1>

      {fixtures.length === 0 ? (
        <p className="text-gray-600">Nenhum jogo encontrado.</p>
      ) : (
        <div className="space-y-4">
          {fixtures.map((f, index) => (
            <Link
              key={index}
              to={`/fixture/${f.fixture.id}`}
              className="block bg-white rounded-xl shadow hover:shadow-lg transition duration-300 p-4 border border-gray-200"
            >
              <div className="grid grid-cols-4 gap-4 items-center">
                {/* Data e Hora */}
                <div className="text-sm text-gray-600">
                  {new Date(f.fixture.date).toLocaleDateString()}<br />
                  <span className="font-semibold">{new Date(f.fixture.date).toLocaleTimeString()}</span>
                </div>

                {/* Time Casa */}
                <div className="flex items-center gap-2">
                  <img src={f.teams.home.logo} alt={f.teams.home.name} className="w-6 h-6" />
                  <span>{f.teams.home.name}</span>
                </div>

                {/* Time Visitante */}
                <div className="flex items-center gap-2">
                  <img src={f.teams.away.logo} alt={f.teams.away.name} className="w-6 h-6" />
                  <span>{f.teams.away.name}</span>
                </div>

                {/* Local */}
                <div className="text-sm text-gray-500">
                  {f.fixture.venue.name || 'Local Indefinido'}
                </div>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
}

export default UpcomingFixtures;
