import React, { useEffect, useState } from 'react';
import api from '../api/api';

function Standings() {
  const [table, setTable] = useState([]);

  useEffect(() => {
    api.get('/standings').then((res) => setTable(res.data));
  }, []);

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4 text-gray-800">üèÜ Standings</h1>

      <div className="overflow-x-auto">
        <table className="min-w-full bg-white shadow-md rounded-lg overflow-hidden">
          <thead className="bg-gray-900 text-white">
            <tr>
              <th className="py-2 px-4 text-left">Pos</th>
              <th className="py-2 px-4 text-left">Team</th>
              <th className="py-2 px-4 text-center">Points</th>
            </tr>
          </thead>
          <tbody>
            {table.map((team) => (
              <tr
                key={team.position}
                className={`border-b ${
                  team.position === 1
                    ? 'bg-green-100 font-bold'
                    : team.position <= 4
                    ? 'bg-gray-100'
                    : ''
                }`}
              >
                <td className="py-2 px-4">{team.position}</td>
                <td className="py-2 px-4">{team.team}</td>
                <td className="py-2 px-4 text-center">{team.points}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Standings;
