import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function CompetitionMatches() {
  const { name } = useParams();
  const [matches, setMatches] = useState({ live: [], today: [] });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/competition/${encodeURIComponent(name)}/matches`);
        setMatches({
          live: response.data.live || [],
          today: response.data.today || [],
        });
      } catch (error) {
        console.error('Erro ao buscar os jogos:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, [name]);

  if (loading) return <p className="text-white">Carregando jogos...</p>;

  return (
    <div className="bg-gray-900 text-white p-4 rounded shadow">
      <h2 className="text-xl font-bold mb-4">Jogos da CompetiÃ§Ã£o: {decodeURIComponent(name)}</h2>

      <section className="mb-6">
        <h3 className="text-lg font-semibold mb-2">ðŸŸ¢ Ao Vivo</h3>
        {matches.live.length === 0 ? (
          <p>Nenhum jogo ao vivo.</p>
        ) : (
          <ul>
            {matches.live.map(match => (
              <li key={match.id} className="mb-2 p-2 bg-gray-800 rounded">
                <strong>{match.home_team}</strong> {match.score} <strong>{match.away_team}</strong>
              </li>
            ))}
          </ul>
        )}
      </section>

      <section>
        <h3 className="text-lg font-semibold mb-2">ðŸ“… Jogos do Dia</h3>
        {matches.today.length === 0 ? (
          <p>Nenhum jogo agendado para hoje.</p>
        ) : (
          <ul>
            {matches.today.map(match => (
              <li key={match.id} className="mb-2 p-2 bg-gray-800 rounded">
                {match.time} - <strong>{match.home_team}</strong> vs <strong>{match.away_team}</strong>
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
}

export default CompetitionMatches;
