import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api/api';

function FixtureDetails() {
  const { id } = useParams();
  const [fixture, setFixture] = useState(null);
  useEffect(() => {
    api.get('/fixture/' + id).then(res => setFixture(res.data));
  }, [id]);
  if (!fixture) return <p>Loading...</p>;
  return (
    <div className="card">
      <h2>{fixture.home} vs {fixture.away}</h2>
      <p>Score: {fixture.score}</p>
      <p>Shots: {fixture.stats.shots}</p>
      <p>Possession: {fixture.stats.possession}</p>
    </div>
  );
}

export default FixtureDetails;
